import React from 'react';
import { useReducer} from 'react';

const reducer = (state, action) => {
    switch (action.type) {
      case "COORDS":
        return [...state, action.payload];

        case "HEIGHTWIDTH":
            return [...state, action.payload];
      default:
        return state;
    }
  }

  export default function GetCoordinates () {

    // const [coords, xAndYs, dispatch] = useReducer(reducer, []);

