import React from 'react';
// import { Component } from 'react'
import { useState } from 'react';

export default function ReduceArea() {

    const[width, setWidth] = useState(window.innerWidth);
    const[height, setHeight] = useState(window.innerHeight);

    const resize = {
        fontSize : '36px'
    }

    const handlingResize = () => {
        setHeight(width)
        setWidth(height);
    }


    return (
    <div onResize={handlingResize}>
    <h1> Total area of window in px {width * height}</h1>
    <p style={resize}> width =  {width} </p>
    <p> height =  {height} </p>
    <p> </p>
    {/* <button onClick={resizing} >click</button> */}
    </div>
    )
}
