import React from 'react';
import './App.css';
// import ScalpEth from './Components/ScalpEth';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

function App() {

const [coiny, setCoiny] = useState("COINBASE:BTCEUR");

// let width = TradingViewWidget.width = '980px'
let heighty = TradingViewWidget.height = '610px'
let themey = TradingViewWidget.theme = 'Dark' ;
let indicators =  TradingViewWidget.studies = [ "TripleEMA@tv-basicstudies",  "RSI@tv-basicstudies"]

return (

<div className="App">
<div className="Navbar">

<button onClick={() => setCoiny("COINBASE:ETHEUR")}>ETH</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}>BTC</button>

</div >
<p>text</p>

    <TradingViewWidget
    symbol={coiny}
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='1'
    studies={indicators}  
    hide_side_toolbar={false}
    width={'980px'}
    height={1000}        
  /> 

</div>

);
}

export default App;
