import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

function App() {

const [coin, setCoin] = useState("COINBASE:BTCEUR");
const [timeFrame, setTimeFrame] = useState(['3', '15', '30' , '180'])
const [indicators, setIndicator] = useState(["BollingerBandsR@tv-basicstudies"]);
const [mode, setMode] = useState(true);

const makeDark = () => { setMode( mode  ? false : true);}

const color = mode  ? 'light' : 'dark';

return (

<div className="App">

<nav className={"navbar " +  color}>
<a className={color} onClick={makeDark}>{ mode ? 'Dark Mode' : 'Light Mode' }</a>

<a className={color} onClick={() => setCoin("COINBASE:ETHEUR")}>ETH</a>
<a className={color} onClick={() => setCoin("COINBASE:BTCEUR")}>BTC</a>
<a className={color} onClick={() => setCoin("COINBASE:BCHEUR")}>BCH</a>
<a className={color} onClick={() => setCoin("COINBASE:LTCEUR")}>LTC</a>

<a className={color} onClick={() => setTimeFrame( [ '3', '15', '30' , '180'])}>Short-Term</a>
<a className={color} onClick={() => setTimeFrame([ '30', '60', '180' , 'D'])}> Mid-Term</a>

<a className={color} onClick={() => setIndicator([ "MAVolumeWeighted@tv-basicstudies", "RSI@tv-basicstudies", "CMF@tv-basicstudies"])}>More Indicators</a>
{/* add links */}
<a className={color} target="_tab" href="https://coinfarm.online/index.asp?tcs=1h">CoinFarm</a>
<a className={color} target="_tab" href="https://coinmarketcap.com/">CMC</a>
<a className={color} target="_tab" href="https://www.bitfinex.com/trading">Bitfinex</a>
<a className={color} target="_tab" href="http://data.bitcoinity.org/markets/bidask_sum/7d/USD/coinbase?bp=5&bu=c&t=m"> Bids vs Sums</a>

</nav>

<div className={"chart-window " +  color}>

    <TradingViewWidget
    symbol={coin}
    theme= {mode ?  'Light': 'Dark' }
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[0]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className={"chart-window " +  color}>

    <TradingViewWidget
    symbol={coin}
    theme= {mode ?  'Light': 'Dark' }
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[1]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className={"chart-window " +  color}>

    <TradingViewWidget
    symbol={coin}
    theme= {mode ?  'Light': 'Dark' }
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[2]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

 </div>

<div className="scalp-chart">

    <TradingViewWidget
    symbol={coin}
    theme= {mode ?  'Light': 'Dark' }
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[3]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

</div>

);
}

export default App;
