import React from 'react';
import { useState } from 'react';

export default function ReduceArea() {

    // uses onMouseMove, 

    const[width, setWidth] = useState(window.innerWidth);
    const[height, setHeight] = useState(window.innerHeight);

    const[x, setX] = useState(window.clientX);
    const[y, setY] = useState(window.clientY);

    window.onresize = () => {
        setHeight(window.innerHeight)
        setWidth(window.innerWidth)
    }

    const showCoords = () => {
        setX(window.event.clientX)
        setY(window.event.clientY)
    }

    return (
    <div onMouseMove={showCoords}>


    <h2> coordinates for X =   { x } coordinates for Y : { y } of mouse</h2>
    <h2> Total area of window in px {width * height}</h2>
    <h2> Width: {width} </h2>
    <h2> Height:  {height} </h2>


    </div>
    )
}
