import React from 'react';
import { useReducer} from 'react';

// optimization. 
// would be good to have orders and another action like add stuff to list separately

const reducer = (state, action) => {
  switch (action.type) {
    case "COORDS":
      return [...state, action.payload];
    case "HEIGHTWIDTH":
          return [...state, action.payload];
    default:
      return state;
  }
}

  export default function GetCoordinates () {

    const [coords, dispatch1] = useReducer(reducer, []);
    const [xAndYs, dispatch2] = useReducer(reducer, []);

    const showCoords = () =>  {

        let x = window.event.clientX
        let y = window.event.clientY
        const coord = { x , y}
        dispatch1({ type: "COORDS", payload: coord})
    }

    window.onresize = () =>  {

        let height = window.innerHeight
        let width = window.innerWidth
        const xAndY = { width, height}
        dispatch2({ type: "HEIGHTWIDTH", payload: xAndY})
    }

    return (

        <div onMouseMove={showCoords} style={{ display: 'flex',  alignItems: 'flex-start'}}>

        <ul style={{ display: 'flex', flexDirection: 'column-reverse', alignItems: 'flex-start' }}>
        {xAndYs.reverse().map(({  width , height }, i) => ( 
             <li key={i}> no {i} and  my { width } and Y { height } </li>
        ))}
        </ul>

        <p> touch here</p>

        <ul style={{ display: 'flex',  flexDirection: 'column-reverse',  alignItems: 'flex-end'}}>
        {coords.reverse().map(({ x, y }, i) => ( 
             <li key={i} > {i} the  X { x } and Y { y } </li>
        ))}
        </ul>

        </div>
    )
  }
