import React from 'react';
import { useReducer} from 'react';

const reducer = (state, action) => {
    switch (action.type) {
      case "COORDS":
        return [...state, action.payload];

        case "DECREMENT":
            return [...state, action.payload];
      default:
        return state;
    }
  }

  export default function GetCoordinates () {

    const [coords, dispatch] = useReducer(reducer, 1);

    const showCoords = () =>  {

        let x = window.event.clientX
        let y = window.event.clientY

        coord = { x , y}

        dispatch({ type: "COORDS", payload: coord})
    }

    const handleDecrement = () =>  {
        let count = count - 1
        dispatch({ type: "DECREMENT", payload: count})
    }

    return (
        <div onMouseMove={showCoords}>
        coords.map( n => ( 
            key{i} <h1> X {x} and Y { y } </h1>
        ))

          <button onClick={handleIncrement}>+1</button>
          <button onClick={handleDecrement}>-1</button>
          {count}
        </div>
    )
  }


