import React from 'react';
import { useState, useRef} from 'react';

function reducer(state, action) {
  switch (action.type) {
    case "ADD_DEET":
      return [...state, action.payload];
    default:
      return state;
  }
}

export default function Example() {

    const inputRef = useRef();
    const heading = useRef();
    const selRef = useRef();

    const [list, setList] = useState([]);
    const [deets, dispatch] = React.useReducer(reducer, []);

    const handleAdd = () => {

        // assign var to use
        let input = inputRef.current;
        let selecting = selRef.current;

        // change style of heading
        heading.current.className = 'off'

        const check = input.value
        console.log(check);

        if (input.value === ''){
            alert('Number please')
        }

        // add reducer 

          if (input.value !== ''){
            const deet = {gender, name, seed}

            dispatch({ type: "ADD_DEET", payload: deet })

            setList([...list, input.value, selecting.value]);
            input.value = ''
        }; 
        console.log(input.value);

        };

    return (
    <div>
    <h1 ref={heading} className={"warning"}> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>

    <select ref={selRef}>
    <option value="Btc">BTC</option>
    <option value="Eth">ETH</option>
    <option value="Ltc">LTC</option>
    </select>

    <input ref={inputRef}  maxLength="6" pattern="/[0-9]/"/>

    <button onClick={handleAdd}> Add item</button>

    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item} 
            </li>
          ))}
        </ul>

        {deets.map(({ coin, item }, i) => (
              <ul key={i} >
                <h1>{coin}</h1><h2> {item} </h2>
              </ul>
            ))}


    </div>
    )
}
