import React from 'react';
import './App.css';
import TradingViewWidget from 'react-tradingview-widget';

function App() {

let width = TradingViewWidget.width = '550px'
let heighty = TradingViewWidget.height = '500px'
let themey = TradingViewWidget.theme = 'Dark' ;
let interval = TradingViewWidget.interval= "60" 
let indicators =  TradingViewWidget.studies = [ "Volume@tv-basicstudies",  "TripleEMA@tv-basicstudies",  "RSI@tv-basicstudies"]

// console.log(typeof Trading)
// console.log(Trading.width)
// console.log(indicators[0])


return (

<div className="App">

<TradingViewWidget

    symbol="BITSTAMP:BTCUSD"
    theme={themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='D'
    studies={indicators}  
    width={width}
    height={heighty}  
        // rangeTypes='ALL' 
      // autoSize='true'   
/>

<TradingViewWidget
    symbol="BITSTAMP:BTCUSD"
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    width={width}
    height={heighty}
    style='8'
    interval='W'
    studies={indicators}  

  />

<TradingViewWidget
    symbol="BITSTAMP:BTCUSD"
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    width={width}
    height={heighty}
    style='8'
    interval='M'
    studies={indicators}          
  />

</div>

);
}

export default App;
