import React from 'react';
import {useEffect} from 'react';

function reducer(state, action) {
    switch (action.type) {
      case "ADD_DEET":
        return [...state, action.payload];
      default:
        return state;
    }
  }

export default function FetchSome() {

    // instead of having setState 5 times, just have this
    const [orders, dispatch] = React.useReducer(reducer, []);

    useEffect(() => {

        fetch("https://api.randomuser.me/")
        .then(results => results.json())
        .then( (data) => {
        const gender = data.results[0].gender
        const name = data.results[0].name.first
        const seed = data.info.seed

        const deets = {gender, name, seed}

        dispatch({ type: "ADD_DEET", payload: deets })

        });
        // can add 'count into array to improve optimization
    }, [deets])

    return (
    <div  style={{backgroundColor : 'grey', padding: '10px'}}>
    <div> 
    {orders.map(({ gender, name, seed }, i) => (
              <ul key={i} >
                <h1>{name}</h1><h2> {gender} </h2><p>{seed}</p>
              </ul>
            ))}
    </div>
    </div>
    )
}
