import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

const links = [ { id:1, text: 'CoinFarm'     ,name:"https://coinfarm.online/index.asp?tcs=1h"},
                { id:3, text: 'CMC'          ,name:"https://coinmarketcap.com/" }, 
                { id:2, text: 'Bitfinex'     ,name: "https://www.bitfinex.com/trading"}, 
                { id:4, text: 'Bids vs Sums' ,name:"http://data.bitcoinity.org/markets/bidask_sum/7d/USD/coinbase?bp=5&bu=c&t=m"}]




function App() {

const [coin, setCoin] = useState("COINBASE:BTCEUR");
const [timeFrame, setTimeFrame] = useState(['3', '15', '30' , '180'])
const [indicators, setIndicator] = useState(["BollingerBandsR@tv-basicstudies"]);

const [mode, setMode] = useState(true);
const color = mode  ? 'light' : 'dark';


const charts = [ { id:1, class:'chart-window', symbol:coin},
                 { id:3, class:'chart-window', symbol:coin}, 
                 { id:2, class:'chart-window', symbol:coin}, 
                 { id:4, class:'scalp-chart' , symbol:coin}]

const coinButtons = [{ name: 'ETH', symbol: "COINBASE:ETHEUR", funct: function(){setCoin(this.symbol)} },
                     { name: 'BTC', symbol: "COINBASE:BTCEUR", funct: function(){setCoin()} },
                     { name: 'BCH', symbol: "COINBASE:BCHEUR", funct: function(){setCoin()} },
                     { name: 'LTC', symbol: "COINBASE:LTCEUR", funct: function(){setCoin()} }]

 return (

<div className={"App " + color}>
<nav className={"navbar " +  color}>

<span className={color} onClick={() => setMode( mode  ? false : true)}>{ mode ? 'Dark Mode' : 'Light Mode' }</span>

{ coinButtons.map(coin => ( <span key={coin.name} className={color} onClick={() => setCoin(coin.symbol)} > {coin.name}</span>))}

<span className={color} onClick={() => setTimeFrame(['3', '15', '30', '180'])}>Short-Term</span>
<span className={color} onClick={() => setTimeFrame(['30', '60', '180', 'D'])}> Mid-Term</span>

<span className={color} onClick={() => setIndicator([ "MAVolumeWeighted@tv-basicstudies", "RSI@tv-basicstudies"])}>1 Indicators</span>
<span className={color} onClick={() => setIndicator([ "RSI@tv-basicstudies", "CMF@tv-basicstudies"])}>2 Indicators</span>
{/* add links */}

{links.map(link => (<a key={link.name} className={color} target="_tab" href={link.name}> {link.text}</a>))}

</nav>

{charts.map( chart => (

<div key={chart.id} className={chart.class}>

<TradingViewWidget
symbol={chart.symbol}
theme= {mode ?  'Light': 'Dark' }
BarStyles='HEIKIN_ASHI'
locale="en"
Style='8'
interval={timeFrame[chart.id]}
studies={indicators}  
hide_side_toolbar={false}
autosize = {true}        
/>  
</div>
)) 
}

</div>

);
}

export default App;
