import React from 'react';
import {useRef} from 'react';
import moment from "moment";

// useReducer means you can pass more items/ variables to component.
// you had a problem with adding select value AND input.value
// you then solved this with the reducer. 

function reducer(state, action) {
  switch (action.type) {
    case "ADD_ITEM":
      return [...state, action.payload];
    default:
      return state;
  }
}

export default function Example() {

    const inputRef = useRef();
    const heading = useRef();
    const selRef = useRef();

    const [list, dispatch] = React.useReducer(reducer, []);

    const handleAdd = () => {

        let input = inputRef.current;
        let selecting = selRef.current;

        if (input.value === ''){
            alert('Number please')
        }

        const time = moment().format("hh:mm")
        const coin = selecting.value
        const item = input.value 

        if (input.value !== ''){
          heading.current.className = 'off'
          const newItem = {coin, item, time}
          dispatch({ type: "ADD_ITEM", payload: newItem })
          input.value = ''
        }  
        }

    return (
    <div>
    <h1 ref={heading} className={"warning"}> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>

    <select ref={selRef}>
    <option className="fa fa-usd" value="Btc">BTC</option>
    <option value="Eth">ETH</option>
    <option value="Ltc">LTC</option>
    </select>

    <input ref={inputRef}  maxLength="6"/>

    <button onClick={handleAdd}> Add item</button>

        {list.map(({ coin, item, time }, i) => (
          <ul key={i} >
            <h2>{ coin } and {item} and { time} </h2>
          </ul>
        ))}


    </div>
    )
}
