import React from 'react';
import { useState, useRef} from 'react';

export default function Example() {

    const inputRef = useRef();
    const [list, setList] = useState([]);

    const handleAdd = () => {

        const input = inputRef.current;

        const check = input.value

        var str = "The best things in life are free";

        var patt = new RegExp(/[0-9]/);
        var res = patt.test(check);
        console.log(res)

        if (input.value !== '' && res === true){
            setList([...list, input.value]);
        }  
          input.value = ''
        };

    return (
    <div>
    <h1> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>

    <input ref={inputRef}  maxLength='6'/>
    <button type="submit" onClick={handleAdd}> Add item</button>

    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item}
            </li>
          ))}
        </ul>
    </div>
    )
}
