import React from 'react'
import './Scalp.css';
import TradingViewWidget from 'react-tradingview-widget';
import { useState } from 'react'

export default function ScalpEth() {

//  const [coin, setCoin] = useState("BITSTAMP:ETHUSD")

//  const changeCoin = () => {
//   setCoin("BITSTAMP:LTCUSD")
// }

// const changeToEtc = () => {
//   setCoin("BITSTAMP:ETCUSD")
// }

let width = TradingViewWidget.width = '980px'
let heighty = TradingViewWidget.height = '610px'
let themey = TradingViewWidget.theme = 'Dark' ;
let indicators =  TradingViewWidget.studies = [ "TripleEMA@tv-basicstudies",  "RSI@tv-basicstudies"]


  const [input, setInput] = useState("BITSTAMP:ETHUSD"); // '' is the initial state value

  let coin = "BITSTAMP:" + input + "USD"

return (

<div className="ScalpChart">

<p>
{/* <a onClick={changeCoin()}>chnge coin</a> */}
{/* <a onClick={}>chnge coin to etc</a> */}
<i class="arrow">NEXT</i>
<input value={input} onInput={e => setInput(e.target.value)}/>
</p>

<TradingViewWidget
    symbol={coin}
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='1'
    studies={indicators}  
    hide_side_toolbar={false}
    width={1000}
    height={1000}        
  />

<TradingViewWidget
    symbol="BITSTAMP:ETHUSD"
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='3'
    studies={indicators}  
    hide_side_toolbar={false}
    width={1000}
    height={1000} 

  />

<TradingViewWidget
    symbol="BITSTAMP:ETHUSD"
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='15'
    studies={indicators}  
    hide_side_toolbar={false}
    width={1000}
    height={1000}       
  />

<TradingViewWidget
    symbol="BITSTAMP:ETHUSD"
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='45'
    studies={indicators}  
    hide_side_toolbar={false}
    width={1000}
    height={1000} 
  />

</div>
)
}
