import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

function App() {

const [coiny, setCoiny] = useState("COINBASE:BTCEUR");

let indicators =  TradingViewWidget.studies = [ "TripleEMA@tv-basicstudies",  "RSI@tv-basicstudies"]

// const[width, setWidth] = useState(window.innerWidth);
//   setWidth(window.innerWidth +'px')


return (

<div className="App">

<div className="Navbar">

<button onClick={() => setCoiny("COINBASE:ETHEUR")}>ETH</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}>BTC</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}> Short-term</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}> Medium-term</button>

</div >

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='3'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='15'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='45'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

 </div>

<div className="scalp-chart">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='60'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

</div>

);
}

export default App;
