import React from 'react';
import './App.css';
import Trading from './Components/Trading';
import TradingTwo from './Components/TradingTwo';
import { useState } from 'react';


   const [isToggleOn, setToggle] = useState(true)

   const hanClick = () => {
        setToggle( isToggleOn  ? false : true)
      }

function App() {

return (

<div className="App">

<button onClick={hanClick}>
      {isToggleOn ? 'ON' : 'OFF'}
    </button>
<Trading/>
{/* <TradingTwo/> */}

</div>

);
}

export default App;
