import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

function App() {

const [coiny, setCoiny] = useState("COINBASE:BTCEUR");
const [timeFrame, setTimeFrame] = useState("COINBASE:BTCEUR");

const [indicators, setIndicator] = useState(["BollingerBandsR@tv-basicstudies"]);

// let indicator =  TradingViewWidget.studies = [ "BollingerBandsR@tv-basicstudies"]

return (

<div className="App">

<div className="Navbar">

<button onClick={() => setCoiny("COINBASE:ETHEUR")}>ETH</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}>BTC</button>


<button onClick={() => setTimeFrame("COINBASE:BTCEUR")}> Short-term</button>
<button onClick={() => setTimeFrame("COINBASE:BTCEUR")}> Medium-term</button>

<button onClick={() => setIndicator([ "MAVolumeWeighted@tv-basicstudies"])}>VWMA</button>

</div >

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='3'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='15'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='45'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

 </div>

<div className="scalp-chart">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='60'
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

</div>

);
}

export default App;
