import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

function App() {

const [coiny, setCoiny] = useState("COINBASE:BTCEUR");

let indicators =  TradingViewWidget.studies = [ "TripleEMA@tv-basicstudies",  "RSI@tv-basicstudies"]

const[width, setWidth] = useState(window.innerWidth);
const[height, setHeight] = useState(window.innerHeight);

window.onresize = () => {
  setHeight(window.innerHeight)
  setWidth(window.innerWidth)
}

const handlingResize = () => {
  setHeight(400)
  setWidth(600)
}



return (

<div className="App">

<div className="Navbar">
<button onClick={() => setCoiny("COINBASE:ETHEUR")}>ETH</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}>BTC</button>
</div >

<div className="scalp-chart">

    <TradingViewWidget
    symbol={coiny}
    theme= {'Dark'}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval='1'
    studies={indicators}  
    hide_side_toolbar={false}
    Width={'500px'}
    Height={'500px'}       
    /> 

<h1> Total area of window in px {width * height}</h1>
    <p> width =  {width} </p>
    <p> height =  {height} </p>

</div>

</div>

);
}

export default App;
