import React from 'react';
import { useState, useEffect} from 'react';

export default function Loop() {




    const handleAdd = () => {
      const [add, setAdd] = useState(-1)



      setAdd(add + 1)
    }


    setInterval(add, 1000);



    return (
    <div>
    <div> 

    <button onClick={handleAdd}></button>
    <h4></h4>
    </div>
    <h1> { add } </h1>
    </div>
    )
}
