import React from 'react';
import { useState } from 'react';


function reducer(state, action) {
    switch (action.type) {
      case "AN_UPDATE":
        return [...state, action.payload];
      default:
        return state;
    }
  }

export default function GetCoordinates() {


    const [orders, dispatch] = React.useReducer(reducer, []);

    React.useEffect (() => {

      const showCoords = () => {
    const x = window.innerWidth
    const y = window.innerHeight
    const width = window.event.clientX
    const height = window.event.clientY
      }

    const updatesXy = { x, y, width, height};

        dispatch({ type: "AN_UPDATE", payload: updatesXy })

    }, []);


    // window.onresize = () => {
    //     setHeight(window.innerHeight)
    //     setWidth(window.innerWidth)
    // }

    // const showCoords = () => {
    //     setX(window.event.clientX)
    //     setY(window.event.clientY)
    // }

    return (
    <div onMouseMove={showCoords}>

    <table>
    <tbody>
            {orders.map(({  x, y, width, height }, i) => (
              <tr  key={i}  >

                <td> {x} </td><td>{y}</td><td>{width}</td><td>{height}</td>

              </tr>
            ))}
          </tbody>
          </table>

    </div>
    )
}
