import React from 'react';
import { useState, useRef, useEffect} from 'react';

export default function Example() {

    const inputRef = useRef();
    const [list, setList] = useState(localStorage.getItem(iteming));

    const handleAdd = () => {

        const input = inputRef.current;
        const iteming = input.value;
        // RegExp pattern if there are numbers
        const patt = new RegExp(/[0-9]/);
        const check = patt.test(iteming);

        if (input.value !== '' && check === true){
            setList([...list, iteming]);
            localStorage.setItem('items', 'iteming')
        }  
          input.value = ''
        };


    // useEffect(() => {
    //   localStorage.setItem('input.value', JSON.stringify(input.value) )

    // }, [item]);

    return (
    <div>
    <h1> Amendment Input </h1>
    <h3> Just numbers, 6 digits, cannot submit empty and remove values</h3>

    <input ref={inputRef}  maxLength='6'/>
    <button type="submit" onClick={handleAdd}> Add item</button>

    <ul>
          {list.reverse().map((item, i) => (
            <li key={i}>{item}
            </li>
          ))}
        </ul>
    </div>
    )
}
