import React from 'react';
// import { Component } from 'react'
import { useState } from 'react';

export default function ReduceArea() {


    const findResize = () => setWidth(window.innerWidth);
    const handleResizes = () => setHeight(window.innerHeight);

    window.addEventListener('resize', findResize);
    window.addEventListener('resize', handleResizes);

    const[width, setWidth] = useState(window.innerWidth);
    const[height, setHeight] = useState(window.innerHeight);

    const handlingResize = () => {
        setHeight(400)
        setWidth(600);
    }

    return (
    <div>
    <h1> Total area of window in px {width * height}</h1>
    <p> width =  {window.innerWidth} </p>
    <p> height =  {window.innerHeight} </p>
    <p> </p>
    <button onClick={handlingResize} >click</button>
    </div>
    )
}
