import React from 'react';
import { useReducer} from 'react';

const reducer = (state, action) => {
    switch (action.type) {
      case "COORDS":
        return [...state, action.payload];
      default:
        return state;
    }
  }

  export default function GetCoordinates () {

    const [coords, dispatch] = useReducer(reducer, []);

    const showCoords = () =>  {

        let x = window.event.clientX
        let y = window.event.clientY

        const coord = { x , y}

        dispatch({ type: "COORDS", payload: coord})
    }


    return (

        <div onMouseMove={showCoords}>
        <p> touch here</p>
        <ul>
        {coords.reverse().map(({ x, y }, i) => ( 
             <li key={i} > X { x } and Y { y } </li>
        ))}
        </ul>
        </div>
    )
  }
