import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

const links = [ {text: 'CoinFarm'     ,name:"https://coinfarm.online/index.asp?tcs=1h"},
                {text: 'CMC'          ,name:"https://coinmarketcap.com/" }, 
                {text: 'Bitfinex'     ,name: "https://www.bitfinex.com/trading"}, 
                {text: 'Bids vs Sums' ,name:"http://data.bitcoinity.org/markets/bidask_sum/7d/USD/coinbase?bp=5&bu=c&t=m"}]



const indicsOne = [ "MAVolumeWeighted@tv-basicstudies", "BollingerBandsR@tv-basicstudies"]
const indicsTwo = [ "RSI@tv-basicstudies",  "CMF@tv-basicstudies"]
const sym =       ["COINBASE:ETHEUR", "COINBASE:BTCEUR", "COINBASE:BCHEUR",  "COINBASE:LTCEUR" ]

const timeFrames = ['3', '15', '30' , '180']
const timeFramesTwo = ['1', '30', '60', '180']
const timeFramesThree = ['30', '60', '120', 'D']


function App() {

const [coin, setCoin] = useState(sym[1]);
const [timeFrame, setTimeFrame] = useState(timeFrames)
const [indicators, setIndicator] = useState(indicsOne);

const [mode, setMode] = useState(true);
const color = mode  ? 'light' : 'dark';

const coinButtons = [{ text: 'ETH', symbol: sym[0], funct: function(){setCoin(this.symbol)} },
                     { text: 'BTC', symbol: sym[1], funct: function(){setCoin(this.symbol)} },
                     { text: 'BCH', symbol: sym[2], funct: function(){setCoin(this.symbol)} },
                     { text: 'LTC', symbol: sym[3], funct: function(){setCoin(this.symbol)} },

                    {text: '1 Indicators', indicators: indicsOne, funct: function(){setIndicator(this.indicators)}  },
                    {text: '2 Indicators', indicators: indicsTwo, funct: function(){setIndicator(this.indicators)}  },
                    {text: 'Short-Term'  , timeFrame: timeFramesTwo, funct: function(){setTimeFrame(this.timeFrame)} },
                    {text: 'Medium-Term' , timeFrame: timeFramesThree, funct: function(){setTimeFrame(this.timeFrame)} },
                    ]

    const charts = [{ id:0, class:'chart-window', symbol:coin},
                    { id:1, class:'chart-window', symbol:coin}, 
                    { id:2, class:'chart-window', symbol:coin}, 
                    { id:3, class:'scalp-chart' , symbol:coin}]

 return (

<div className={"App " + color}>
<nav className={"navbar " + color}>
<span className={color} onClick={() => setMode( mode  ? false : true)}>{ mode ? 'Dark Mode' : 'Light Mode' }</span>

{coinButtons.map(coin => ( <span key={coin.text} className={color} onClick={() => coin.funct()} > {coin.text}</span>))}

{links.map(link => (<a key={link.name} className={color} target="_tab" href={link.name}> {link.text}</a>))}
</nav>

{charts.map( chart => (

<div key={chart.id} className={chart.class}>
<TradingViewWidget
symbol={chart.symbol}
theme= {mode ?  'Light': 'Dark' }
BarStyles='HEIKIN_ASHI'
locale="en"
Style='8'
interval={timeFrame[chart.id]}
studies={indicators}  
hide_side_toolbar={false}
autosize = {true}        
/>  
</div>
)) 
}

</div>

);
}

export default App;
