import React from 'react';
import './App.css';
import TradingViewWidget, { Themes } from 'react-tradingview-widget';

function App() {

  const Trading = new TradingViewWidget

let small = Trading.width = '33vw'
let heighty = Trading.height = '50vh'
let themey = Trading.theme = 'Dark'
let indicators =  Trading.studies = [ "Volume@tv-basicstudies",  "TripleEMA@tv-basicstudies",  "MASimple@tv-basicstudies"]

// console.log(Trading)
// console.log(typeof Trading)
// console.log(Trading.width)
// console.log(indicators[0])

return (

<div className="App">

<TradingViewWidget
    className="Widget"
    symbol="BITSTAMP:BTCUSD"
    theme={themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    width={small}
    height={heighty}
    style='8'
    interval="180"
    studies={indicators}

/>


<TradingViewWidget
    symbol="BITSTAMP:ETHUSD"
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    width={small}
    height={heighty}
    style='8'
    interval="180"
    studies={indicators}           
  />

<TradingViewWidget
    symbol="BITSTAMP:LTCUSD"
    theme= {themey}
    // theme={Themes.DARK}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    width={small}
    height={heighty}
    style='8'
    interval="180"
    studies={indicators}         
  />






</div>

);
}

export default App;
