import React from 'react';
import './App.css';
// import ScalpEth from './Components/ScalpEth';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';


let width = TradingViewWidget.width = '980px'
let heighty = TradingViewWidget.height = '610px'
let themey = TradingViewWidget.theme = 'Dark' ;
let indicators =  TradingViewWidget.studies = [ "TripleEMA@tv-basicstudies",  "RSI@tv-basicstudies"]

const [input, setInput] = useState("ETH"); // '' is the initial state value

let coin = "COINBASE:" + input + "EUR"

function App() {

const [count, setCount] = useState(1);

return (

<div className="App">
<div className="Navbar">



</div >

<p>
<i class="arrow">NEXT</i>
<input value={input} onInput={e => setInput(e.target.value)}/>
<ruby>
漢 <rt> ㄏㄢˋ </rt>
</ruby>
</p>

    <div>
    <p>You clicked times
    {count < 5 ? <button onClick={() => setCount(count + 1)}>
      Click me
    </button> : <span></span>}
    </p>
    </div>

    {count === 2 ? <TradingViewWidget
    symbol={coin}
    theme= {themey}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    style='8'
    interval='1'
    studies={indicators}  
    hide_side_toolbar={false}
    width={1000}
    height={1000}        
  /> : false}

</div>



);
}

export default App;
