import React from 'react';
import { useState } from 'react';

export default function ReduceArea() {

    // refactored code  - window.resize
    // 

    const[width, setWidth] = useState(window.innerWidth);
    const[height, setHeight] = useState(window.innerHeight);

    window.onresize = () => {
        setHeight(window.innerHeight)
        setWidth(window.innerWidth)
    }

    const handlingResize = () => {
        setHeight(400)
        setWidth(600)
    }

    const[x, setX] = useState(window.clientX);
    const[y, setY] = useState(window.clientY);

    const showCoords = () => {
        setX(window.event.clientX)
        setY(window.event.clientY)

        console.log( 'window size ' , window.event.clientX)
        console.log('window size ' , window.event.clientY)
    }

    return (
    <div onClick={showCoords}>
    <p>  { x } and { y }</p>
    <h1> Total area of window in px {width * height}</h1>
    <p> width =  {width} </p>
    <p> height =  {height} </p>
    <p> </p>
    <button onClick={handlingResize} >click</button>
    </div>
    )
}
