import React from 'react';
import { useState } from 'react';


function reducer(state, action) {
    switch (action.type) {
      case "ADD_ORDER":
        return [...state, action.payload];
      default:
        return state;
    }
  }

export default function GetCoordinates() {


    const [orders, dispatch] = React.useReducer(reducer, []);

    React.useEffect (() => {
    const x = window.innerWidth
    const y = window.innerHeight
    const width = window.clientX
    const height = window.clientY

    const order = { x, y, width, height};

        dispatch({ type: "ADD_ORDER", payload: order })

    }, []);

    // uses onMouseMove, window.event.client 

    // const[width, setWidth] = useState(window.innerWidth);
    // const[height, setHeight] = useState(window.innerHeight);

    // const[x, setX] = useState(window.clientX);
    // const[y, setY] = useState(window.clientY);

    // window.onresize = () => {
    //     setHeight(window.innerHeight)
    //     setWidth(window.innerWidth)
    // }

    // const showCoords = () => {
    //     setX(window.event.clientX)
    //     setY(window.event.clientY)
    // }

    return (
    <div>

    {/* <h2> coordinates for X =   { x } coordinates for Y : { y } of mouse</h2>
    <h2> Total area of window in px {width * height}</h2>
    <h2> Width: {width} </h2>
    <h2> Height:  {height} </h2> */}

    <table>
    <tbody>
            {orders.map(({  x, y, width, height }, i) => (
              <tr  key={i}  >

                <td> {x} </td><td>{y}</td><td>{width}</td><td>{height}</td>

              </tr>
            ))}
          </tbody>
          </table>

    </div>
    )
}
