import React from 'react';
import './App.css';
import { useState } from 'react'
import TradingViewWidget from 'react-tradingview-widget';

function App() {

const [coiny, setCoiny] = useState("COINBASE:BTCEUR");
const [timeFrame, setTimeFrame] = useState(['3', '15', '45' , '180'])
const [indicators, setIndicator] = useState(["BollingerBandsR@tv-basicstudies"]);

const [mode, setMode] = useState("Light");

const makeDark = () => {
  setMode(  mode  ? false : true);
}

return (

<div className="App">

<div className="Navbar">

<button onClick={() => setCoiny("COINBASE:ETHEUR")}>ETH</button>
<button onClick={() => setCoiny("COINBASE:BTCEUR")}>BTC</button>
<button onClick={() => setTimeFrame( [ '3', '15', '45' , '180'])}> Short-term</button>
<button onClick={() => setTimeFrame([ '45', '60', '180' , 'D'])}> Medium-term</button>

<button onClick={() => setIndicator([ "MAVolumeWeighted@tv-basicstudies"])}>VWMA</button>

<button onClick={makeDark}>{ mode? 'Dark' : 'Light'  }</button>

{/* add links */}
<button><a   href="https://coinfarm.online/index.asp?tcs=1h">CoinFarm</a></button>
<button><a  href="https://coinmarketcap.com/">CMC</a></button>
<button><a  href="https://www.bitfinex.com/trading">Bitfinex</a></button>
<button><a  href="http://data.bitcoinity.org/markets/bidask_sum/7d/USD/coinbase?bp=5&bu=c&t=m"> Bids vs Sums</a></button>



</div >

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {mode}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[0]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {mode}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[1]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

<div className="chart-window">

    <TradingViewWidget
    symbol={coiny}
    theme= {mode}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[2]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

 </div>

<div className="scalp-chart">

    <TradingViewWidget
    symbol={coiny}
    theme= {mode}
    BarStyles='HEIKIN_ASHI'
    locale="en"
    Style='8'
    interval={timeFrame[3]}
    studies={indicators}  
    hide_side_toolbar={false}
    autosize      
    /> 

</div>

</div>

);
}

export default App;
