import React from 'react';
import { useState } from 'react';


function reducer(state, action) {
    switch (action.type) {
      case "AN_UPDATE":
        return [...state, action.payload];
      default:
        return state;
    }
  }

export default function Coordinates() {


    const [orders, dispatch] = React.useReducer(reducer, []);

    React.useEffect (() => {

    // const showCoords = () => {

    const x = window.innerWidth
    const y = window.innerHeight
    const width = window.clientX
    const height = window.clientY
      // }

    const updatesXy = { x, y, width, height};

        dispatch({ type: "AN_UPDATE", payload: updatesXy })

    }, []);

    return (
    <div>
      {/* <p> onMouseMove={showCoords}</p> */}
    <table>
    <tbody>
            {orders.map(({  x, y, width, height }, i) => (
              <tr  key={i}  >
                <td> {x} </td><td>{y}</td><td>{width}</td><td>{height}</td>

              </tr>
            ))}
          </tbody>
          </table>

    </div>
    )
}
